import './ExpensesFilter.css';
import { useState } from 'react';
const ExpensesFilter = () => {

  const [enteredYear, setEnteredYear] = useState("");

const yearChangeHandler =(event) => {
  setEnteredYear = (event.target.value)
}

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onChange={yearChangeHandler}>2022</option>
          <option value='2021' onChange={yearChangeHandler}>2021</option>
          <option value='2020' onChange={yearChangeHandler}>2020</option>
          <option value='2019' onChange={yearChangeHandler}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
